import './index.css'
import avatar from './images/avatar.png'
import React from 'react'
// import { tab } from '@testing-library/user-event/dist/tab'
import { v4 as uuid } from 'uuid'

// 时间格式化
function formatDate (time) {
  return `${time.getFullYear()}-${time.getMonth()}-${time.getDate()} ${time.toTimeString().slice(0, 8)}`
}

class App extends React.Component {
  state = {
    // hot: 热度排序  time: 时间排序
    tabs: [
      {
        id: 1,
        name: '热度',
        type: 'hot'
      },
      {
        id: 2,
        name: '时间',
        type: 'time'
      }
    ],
    active: 'hot',
    list: [
      {
        id: 1,
        author: '刘德华',
        comment: '给我一杯忘情水',
        time: new Date('2021-10-10 09:09:00'),
        // 1: 点赞 0：无态度 -1:踩
        attitude: 1
      },
      {
        id: 2,
        author: '周杰伦',
        comment: '哎哟，不错哦',
        time: new Date('2021-10-11 09:09:00'),
        // 1: 点赞 0：无态度 -1:踩
        attitude: 0
      },
      {
        id: 3,
        author: '五月天',
        comment: '不打扰，是我的温柔',
        time: new Date('2021-10-11 10:09:00'),
        // 1: 点赞 0：无态度 -1:踩
        attitude: -1
      }
    ],
    comment: '',
  }

  switchTab = (type) => {
    console.log('switchTab');
    this.setState({
      active: type,
      
    });

    if(this.state.active === 'time') {
      this.state.list.sort((a, b) => {
        return b.time - a.time
      })
    }
    }

  handleChange = (e) => {
    this.setState({ comment: e.target.value })
  }

  post = (e) => {
    console.log('post');
    this.setState({
      list: [...this.state.list, {
        // id: this.state.list.length + 1,
        id: uuid(),
        author: '匿名用户',
        comment: this.state.comment,
        time: new Date(),
        // 1: 点赞 0：无态度 -1:踩
        attitude: 0
      }],
      comment: ''
    })
  }

  deletePost = (id) => {
    console.log('deletePost');
    this.setState({
      list: this.state.list.filter(item => item.id !== id)
    })
  }

  toggleAttitudeLike = (item) => {
    console.log('toggleAttitudeLike');
    const { id, attitude } = item;
    this.setState({
      list: this.state.list.map(item => {
        if (item.id === id) {
          return {
            ...item,
            attitude: attitude === 1 ? 0 : 1,
          }}
        else{
          return item;
        }
        })
    })
  }
  toggleAttitudeHate = (item) => {
    console.log('toggleAttitudeHate');
    const { id, attitude } = item;
    this.setState({
      list: this.state.list.map(item => {
        if (item.id === id) {
          item.attitude = attitude === -1 ? 0 : -1;
        }
        return item;
      })
    })
  }

  render () {
    return (
      <div className="App">
        <div className="comment-container">
          {/* 评论数 */}
          <div className="comment-head">
            <span>{this.state.list.length} 评论</span>
          </div>
          {/* 排序 */}
          <div className="tabs-order">
            <ul className="sort-container">
              {
                this.state.tabs.map(tab => (
                  <li
                    key={tab.id}
                    onClick={() => this.switchTab(tab.type)}
                    className={tab.type === this.state.active ? 'on' : ''}
                  >按{tab.name}排序</li>
                ))
              }
            </ul>
          </div>

          {/* 添加评论 */}
          <div className="comment-send">
            <div className="user-face">
              <img className="user-head" src={avatar} alt="" />
            </div>
            <div className="textarea-container">
              <textarea
                cols="80"
                rows="5"
                placeholder="请输入评论"
                value={this.state.comment}
                onChange={this.handleChange}
                className="ipt-txt"
              />
              <button onClick={this.state.comment !== ''? this.post: null} className="comment-submit">发表评论</button>
            </div>
            <div className="comment-emoji">
              <i className="face"></i>
              <span className="text">表情</span>
            </div>
          </div>

          {/* 评论列表 */}
          <div className="comment-list">
            {
              this.state.list.map(item => (
                <div className="list-item" key={item.id}>
                  <div className="user-face">
                    <img className="user-head" src={avatar} alt="" />
                  </div>
                  <div className="comment">
                    <div className="user">{item.author}</div>
                    <p className="text">{item.comment}</p>
                    <div className="info">
                      <span className="time">{formatDate(item.time)}</span>
                      <span onClick={()=> this.toggleAttitudeLike(item)} className={item.attitude === 1 ? 'like liked' : 'like'}>
                        <i className="icon" />
                      </span>
                      <span onClick={() => this.toggleAttitudeHate(item)} className={item.attitude === -1 ? 'hate hated' : 'hate'}>
                        <i className="icon" />
                      </span>
                      <span className="reply btn-hover" onClick={() => this.deletePost(item.id)}>删除</span>
                    </div>
                  </div>
                </div>
              ))
            }
          </div>
        </div>
      </div>)
  }
}


export default App
